mapper.select:
select count(*)
from member
where member_email = #{email}

select member_id
from member
where member_email = #{email}

select count(*)
from member
where member_email = #{memberEmail}
	and member_id = #{memberId}

mapper.update:
update member
set member_pwd = #{memberPwd}
where member_email = #{memberEmail}
	and member_id = #{memberId}
